import './styles.css'
import React, {useState} from 'react'




const Main = () => {

const [flameColorState, setFlameColorState] = useState({color1: 'yellow', color2: 'red'}) 
const [currentElementState, setCurrentElementState] = useState('none')

// Note for future, setting state works a lot better as one object with 2 colors as properties, rather than changing 2 separate states onClick

const [mode, setMode] = useState('sandbox')




const flameColors = [
    {
        name: 'Lithium',
        symbol: 'Li',
        color: {color1: 'crimson',
                color2: 'black'}               
    },
    {
        name: 'Sodium',
        symbol: 'Na',
        color: {color1: 'yellow',
                color2: 'black'}
    },
    {
        name: 'Potassium',
        symbol: 'K',
        color: {color1: 'mediumpurple',
                color2: 'black'}
    },
    {
        name: 'Calcium',
        symbol: 'Ca',
        color: {color1: 'orange',
                color2: 'black'}
    },
    {
        name: 'Copper',
        symbol: 'Cu',
        color: {color1: 'green',
                color2: 'black'}
    },
    {
        name: 'Selenium',
        symbol: 'Se',
        color: {color1: 'blue',
                color2: 'black'}
    }
]


const Button = () => {
    return (
        flameColors.map((element) => 
        <button class="element-button" 
        onClick={() => mode === 'sandbox' ? setFlameColorState(element.color) : checkAnswer(element.name)}>{element.name}</button>
    ))
}

const checkAnswer = (name) => {
    if (name === currentElementState){
        console.log('correct answer')
    }
    else {
        console.log('wrong answer')
    }
}   

/* calling the changeMode function makes way more sense, this way I can reset colours to default within the function, rather than just setting the state onclick
with an inline function when clicking the buttons.  also means I can just have one changemode function rather than two separate inline functions! */

const changeMode = () => {
    mode === 'sandbox' ? setMode('test') : setMode('sandbox')
    setFlameColorState({color1: 'yellow', color2: 'red'})
}









if (mode === 'sandbox'){
    return (
        <div className = 'main'>
            <h2>Sandbox Mode</h2>
        <div class="main-grid">
            <div class="buttons-grid">
            <Button />
            </div>
            <div class="bunsen-div">
                bunsen picture here, the colour will be {flameColorState.color1} and {flameColorState.color2} like this:
                <div class="bunsen-flame-color" style={{backgroundColor: `${flameColorState.color1}`, border: `2px solid ${flameColorState.color2}`}}>
                    Flame Color
                </div>
            </div>


        </div>

        <button onClick={changeMode}>Test yourself</button>

        </div>
    )}

if (mode === 'test'){
    return (
        <div className = 'main'>
            <h2>Test Yourself</h2>
            <div className="main-grid">
            <div className="buttons-grid">
            <Button />
            </div>
            <div className="bunsen-div">
                bunsen picture here, the colour will be {flameColorState.color1} and {flameColorState.color2} like this:
                <div className="bunsen-flame" style={{backgroundColor: `${flameColorState.color1}`, border: `2px solid ${flameColorState.color2}`}}>
                    Flame Color
                </div>
            </div>


        </div>
            <button onClick={changeMode}>Sandbox mode</button>
        </div>

    )}

}

export default Main
