import './styles.css'
import React, {useState} from 'react'




const Main = () => {

const [flameColor1State, setFlameColor1State] = useState('yellow') 
const [flameColor2State, setFlameColor2State] = useState('red')
const [mode, setMode] = useState('sandbox')




const flameColors = [
    {
        name: 'Lithium',
        symbol: 'Li',
        color1: 'crimson',
        color2: 'black'
    },
    {
        name: 'sodium',
        symbol: 'Na',
        color1: 'yellow',
        color2: 'black'
    },
    {
        name: 'Potassium',
        symbol: 'K',
        color1: 'mediumpurple',
        color2: 'black'
    },
    {
        name: 'Calcium',
        symbol: 'Ca',
        color1: 'orange',
        color2: 'black'
    },
    {
        name: 'Copper',
        symbol: 'Cu',
        color1: 'green',
        color2: 'black'
    },
    {
        name: 'Selenium',
        symbol: 'Se',
        color1: 'blue',
        color2: 'black'
    }
]


const Button = () => {
    for (element of flameColors){
        return (
        <button class="element-button">{element.name}</button>
    )
}
}


if (mode === 'sandbox'){
    return (
        <div className = 'main'>

        <div class="main-grid">
            <div class="buttons-grid">
            <Button />
            </div>
            <div class="bunsen-div">
                bunsen picture here, the colour will be {flameColor1State} like this:
                <div class="bunsen-flame-color" style={{backgroundColor: `${flameColor1State}`, border: `2px solid ${flameColor2State}`}}>
                    Flame Color
                </div>
            </div>




        </div>


        </div>
    )}

if (mode === 'test'){
    return (
        <div className = 'main'>
            Test mode
        </div>
    )}

}

export default Main
