import './styles.css'
import React, {useState} from 'react'




const Main = () => {

const [flameColorState, setFlameColor1State] = useState({color1: 'yellow', color2: 'red'}) 
const [mode, setMode] = useState('sandbox')




const flameColors = [
    {
        name: 'Lithium',
        symbol: 'Li',
        color: {color1: 'crimson',
                color2: 'black'}               
    },
    {
        name: 'Sodium',
        symbol: 'Na',
        color: {color1: 'yellow',
                color2: 'black'}
    },
    {
        name: 'Potassium',
        symbol: 'K',
        color: {color1: 'mediumpurple',
                color2: 'black'}
    },
    {
        name: 'Calcium',
        symbol: 'Ca',
        color: {color1: 'crimson',
                color2: 'black'}
    },
    {
        name: 'Copper',
        symbol: 'Cu',
        color: {color1: 'crimson',
                color2: 'black'}
    },
    {
        name: 'Selenium',
        symbol: 'Se',
        color: {color1: 'crimson',
                color2: 'black'}
    }
]


const Button = () => {
    return (
        flameColors.map((element) => 
        <button class="element-button" 
        onClick={() => setFlameColorState(element.color)}>{element.name}</button>
    ))
}









if (mode === 'sandbox'){
    return (
        <div className = 'main'>

        <div class="main-grid">
            <div class="buttons-grid">
            <Button />
            </div>
            <div class="bunsen-div">
                bunsen picture here, the colour will be {flameColor1State} like this:
                <div class="bunsen-flame-color" style={{backgroundColor: `${flameColor1State}`, border: `2px solid ${flameColor2State}`}}>
                    Flame Color
                </div>
            </div>




        </div>


        </div>
    )}

if (mode === 'test'){
    return (
        <div className = 'main'>
            Test mode
        </div>
    )}

}

export default Main
