        color2: 'black'
    },
    {
        element: 'Selenium',
        symbol: 'Se',
        color1: 'blue',
        color2: 'black'
    }
]

if (mode === 'sandbox'){
    return (
        <div className = 'main'>

        <div class="grid">
            <p>Buttons here</p>




        </div>


        </div>
    )}

if (mode === 'test'){
    return (
        <div className = 'main'>
            Test mode
        </div>
    )}

}

export default Main
